module.exports = {
    "rules": {
        "strict": "off"
    },
    "globals": {
        "snippets": false
    }
};

/**
 * Adds an script from the given src to the page.
 * 
 * WARNING: This is incredibly dangerous. Only use this if you are sure
 * you can trust the given src.
 * 
 * @param {DOCUMENT} document The document object.
 * @param {string} src The src of the script to be added.
 * 
 * @return {Promise} A promise that resolves when the script finishes loading.
 */
const addScript = function(document, src) {
    if (typeof src !== "string") throw new Error("Invalid src argument: must be a string.");
    return new Promise(resolve => {
        const script = document.createElement('script');
        script.setAttribute('src', src);
        script.onload = resolve;
        document.body.appendChild(script);
    });
};

/**
 * Adds an script with the given content to the page.
 * 
 * WARNING: This is incredibly dangerous. Only use this if you know
 * exactly what you're doing, and if the content is a hardcoded string,
 * and definitely nothing that comes from user input.
 * 
 * The given content starts executing immediately. This function returns
 * immediately unless the given content code hangs or throws (in which
 * case this function will also hang/throw respectively).
 * 
 * @param {DOCUMENT} document The document object.
 * @param {string} content The content to be put in the script.
 */
addScript.inline = function(document, content) {
    if (typeof content !== "string") throw new Error("Invalid content argument: must be a string.");
    return new Promise(resolve => {
        const script = document.createElement('script');
        try {
            script.appendChild(document.createTextNode(content));
        } catch (e) {
            script.text = content;
        }
        document.body.appendChild(script);
        resolve();
    });
};

/* test */ module.exports = {
    snippet: addScript,
    snippetName: "addScript",
    snippetTest: t => {
        t.is(typeof addScript, "function");
        t.is(typeof addScript.inline, "function");
    }
};

/**
 * Checks whether the given array contains the given value(s) or not. The value(s) is/are
 * searched in O(n*k) using javascript strict equality operator (`===`), where n is the
 * array length and k is the amount of passed values.
 * 
 * This function returs true if and only if all given values are present in the array.
 * 
 * @param {Array} array The array to be checked
 * @param {...*} values The value(s) to be searched in the array
 * 
 * @returns {boolean} A boolean that indicates whether the array contains the value.
 * 
 * @example
 * 
 * ```
 * arrayHas([1, 2, 3], 1); // true
 * arrayHas([1, 2, 3], 2, 1, 1); // true
 * arrayHas([1, 2, 3], 2, 4, 1); // false
 * arrayHas([1, 2, 3], [1, 2, 3]); // false
 * ```
 */
const arrayHas = function(array, ...values) {
    return values.every(value => array.some(obj => obj === value));
};

/* test */ module.exports = {
    snippet: arrayHas,
    snippetName: "arrayHas",
    snippetTest: t => {
        t.is(arrayHas([1, 2, 3], 1), true);
        t.is(arrayHas([1, 2, 3], 2, 1, 1), true);
        t.is(arrayHas([1, 2, 3], 2, 4, 1), false);
        t.is(arrayHas([1, 2, 3], [1, 2, 3]), false);
    }
};

/**
 * Creates an ES6 Map object from an array, where each object's key is defined
 * by the keyMaker function.
 * 
 * @param {Array} array The source array.
 * @param {function} keyMaker The function that takes an element of the array and
 * returns a key to be used in the map for that element. If this function returns
 * a repeated key for two different objects, the former will be overwritten and
 * only the latter value will remain in the map.
 * 
 * @returns {Map} A map whose keys are the results of calling keyMaker on the array
 * elements and whose values are the array elements.
 * 
 * @example
 * 
 * ```
 * let array = [{ a: 3 }, { a: 1, d: 2 }, { a: 5, b: 3 }];
 * let map = arrayToMap(array, x => x.a);
 * map.get(3); // { a: 3 }
 * map.get(1); // { a: 1, d: 2 }
 * map.get(5); // { a: 5, b: 3 }
 * ```
 */
const arrayToMap = function(array, keyMaker) {
    return new Map(array.map(x => [keyMaker(x), x]));
};

/* test */ module.exports = {
    snippet: arrayToMap,
    snippetName: "arrayToMap",
    snippetTest: t => {
        const array = [{ a: 3 }, { a: 1, d: 2 }, { a: 5, b: 3 }];
        const map = arrayToMap(array, x => x.a);
        t.deepEqual(map.get(3), { a: 3 });
        t.deepEqual(map.get(1), { a: 1, d: 2 });
        t.deepEqual(map.get(5), { a: 5, b: 3 });
    }
};

examples:
```
let array = [{ a: 3 }, { a: 1, d: 2 }, { a: 5, b: 3 }];
let map = arrayToMap(array, x => x.a);
map.get(3); // { a: 3 }
map.get(1); // { a: 1, d: 2 }
map.get(5); // { a: 5, b: 3 }
```

