/**
 * Cryptographycally secure equivalent to Math.random() in browsers.
 * 
 * @return {number} A number between 0 (inclusive) and 1 (exclusive)
 */
const browserSecureRandom = function() {
    /* global window */
    const temp = new Uint32Array(1);
    window.crypto.getRandomValues(temp);
    return temp / 0x100000000;
};

/* test */ module.exports = {
    snippet: browserSecureRandom,
    snippetName: "browserSecureRandom",
    snippetTest: t => {
        t.pass();
    }
};

/**
 * Takes an array of functions and calls them sequentially. If any of these
 * functions return a promise, the promise is waited for, before calling the next
 * functions.
 * 
 * @param {function[]} functions The array of functions to be called sequentially
 * 
 * @return {Promise} A promise that resolves when the last call is done and
 * rejects with an error if any of the functions throw an error (in which case
 * the sequence is terminated immediately).
 */
const callSequentially = function(functions) {
    let promiseChain = Promise.resolve();
    functions.forEach(f => {
        promiseChain = promiseChain.then(f);
    });
    return promiseChain;
};

/* test */ module.exports = {
    snippet: callSequentially,
    snippetName: "callSequentially",
    snippetTest: t => {
        let temp = 100;
        return callSequentially([
            () => { temp += 5; },
            () => { temp *= 2; },
            () => snippets.wait(50).then(() => { temp *= 3; }),
            () => { temp += 11; }
        ]).then(() => {
            t.is(temp, 641);
        });
    }
};

/**
 * Colorizes `console.warn` and `console.error` and adds the extra methods
 * `console.blue`, `console.green` and `console.gray`.
 * 
 * @requires npm:chalk
 */
const colorizeConsole = function() {
    const chalk = require("chalk");

    function colorize(args, color) {
        return args.map(arg => {
            if (typeof arg !== "string") return arg;
            return chalk[color](arg);
        });
    }

    const originalConsoleWarn = console.warn.bind(console);
    const originalConsoleError = console.error.bind(console);

    console.warn = (...args) => originalConsoleWarn(...colorize(args, "yellow"));
    console.error = (...args) => originalConsoleError(...colorize(args, "red"));
    console.blue = (...args) => console.log(...colorize(args, "blue"));
    console.green = (...args) => console.log(...colorize(args, "green"));
    console.gray = (...args) => console.log(...colorize(args, "gray"));
};

/* test */ module.exports = {
    snippet: colorizeConsole,
    snippetName: "colorizeConsole",
    snippetTest: t => {
        t.pass();
    }
};

/**
 * Counts how many times each element of the array occurs in it.
 * 
 * @param {Array} array
 * 
 * @return {Map} A map that maps elements to the amount of occurrences.
 * 
 * @example
 * 
 * ```
 * let array = ["a", 1, null, "b", null, "b", "a", "a"];
 * let countMap = countOccurrences(array);
 * countMap.get("a"); // 3
 * countMap.get(1); // 1
 * countMap.get(null); // 2
 * countMap.get("b"); // 2
 * ```
 */
const countOccurrences = function(array) {
    const result = new Map();
    for (const element of array) {
        if (result.has(element)) {
            result.set(element, result.get(element) + 1);
        } else {
            result.set(element, 1);
        }
    }
    return result;
};

/* test */ module.exports = {
    snippet: countOccurrences,
    snippetName: "countOccurrences",
    snippetTest: t => {
        const array = ["a", 1, null, "b", null, "b", "a", "a"];
        const countMap = countOccurrences(array);
        t.is(countMap.get("a"), 3);
        t.is(countMap.get(1), 1);
        t.is(countMap.get(null), 2);
        t.is(countMap.get("b"), 2);
    }
};

/**
 * Produces an array which contains a subset of the given array, deduplicating
 * elements by the values each yield when passed to the valuePicker function.
 * In other words, produces a subset of the array by keeping only one element
 * per value yielded when passed to the valuePicker function.
 * 
 * @requires snippet:replace-array
 * 
 * @param {Array} array The array to the deduplicated
 * 
 * @param {function} valuePicker A function that when applied to each value of
 * the array returns another value to be used for the deduplication. This function
 * is called only once per value.
 * 
 * @param {boolean} [inPlace=false] If true, alters the given array instead of
 * creating a new one.
 * 
 * @returns {Array} The resulting array (if inPlace is true, this is the same as
 * the passed array, otherwise it is a new array).
 * 
 * @example
 * 
 * ```
 * let array = [{ a: 1, b: 2 }, { a: 3, b: 2 }];
 * deduplicateBy(array, x => x.b, true);
 * console.log(array); // [{ a: 1, b: 2 }]
 * ```
 */
const deduplicateBy = function(array, valuePicker, inPlace = false) {
    let result = [];
    const helperArray = array.map(x => ({ value: x, picked: valuePicker(x) }));
    helperArray.forEach(x => {
        if (!result.find(r => r.picked === x.picked)) result.push(x);
    });
    result = result.map(x => x.value);
    if (inPlace) return snippets.replaceArray(array, result);
    return result;
};

/* test */ module.exports = {
    snippet: deduplicateBy,
    snippetName: "deduplicateBy",
    snippetTest: t => {
        const array = [{ a: 1, b: 2 }, { a: 3, b: 2 }];
        deduplicateBy(array, x => x.a, true);
        t.deepEqual(array, [{ a: 1, b: 2 }, { a: 3, b: 2 }]);
        deduplicateBy(array, x => x.b, true);
        t.deepEqual(array, [{ a: 1, b: 2 }]);
    }
};

examples:
```
let array = [{ a: 1, b: 2 }, { a: 3, b: 2 }];
deduplicateBy(array, x => x.b, true);
console.log(array); // [{ a: 1, b: 2 }]
```

