/**
 * Deep clone an object without circular references. Everything whose typeof is
 * not "object" will be copied by reference (without cloning).
 * 
 * @requires npm:is-circular^1.0.0
 * @requires snippet:map-object
 * 
 * @param {object} object The object to be cloned
 * 
 * @return {object} The cloned object
 * 
 * @throws {Error} If the object has circular references
 */
const deepClone = (() => {
    const isCircular = require('is-circular');
    return function(obj) {
        if (obj === null) return null;
        if (typeof obj !== "object") return obj;
        if (isCircular(obj)) throw new Error("Unable to deep clone with circular references.");
        if (Array.isArray(obj)) return obj.map(deepClone);
        return snippets.mapObject(obj, deepClone);
    };
})();

/* test */ module.exports = {
    snippet: deepClone,
    snippetName: "deepClone",
    snippetTest: t => {
        const obj = { a: 1, b: 2, c: [{ d: 3, e: 4 }] };
        const clone = deepClone(obj);
        t.not(clone, obj);
        t.not(clone.c, obj.c);
        t.not(clone.c[0], obj.c[0]);
        t.deepEqual(clone, obj);
        obj.f = obj;
        t.throws(() => {
            deepClone(obj);
        });
    }
};

/**
 * Downloads a file from a given URL and saves it.
 * 
 * @requires fs-jetpack^2.2.0
 * @requires request^2.88.0
 * 
 * @param {string} sourceURL The URL containing the file to be downloaded.
 * @param {string} [destinationFilePath] The path (including filename) for
 * the file to be saved. If omitted, defaults to the same filename given in
 * the sourceURL.
 * 
 * @return {Promise} A promise that resolves when everything is done or reject
 * with any error that happens in the process.
 */
const downloadFile = function(sourceURL, destinationFilePath = null) {
    const jetpack = require("fs-jetpack");
    const request = require("request");

    return Promise.resolve().then(() => {
        // Ensure destinationFilePath is valid
        if (!destinationFilePath) {
            destinationFilePath = /[^\\/]*$/.exec(sourceURL);
            if (destinationFilePath) destinationFilePath = destinationFilePath[0];
        }
        if (!destinationFilePath) throw new Error("Unable to parse default destination file name.");
        if (typeof destinationFilePath !== "string") throw new TypeError("Invalid destinationFilePath: must be a string.");
    }).then(() => {
        // Ensure the path to the file exists and that the file does not
        // (i.e. that it gets overwritten if exists).
        return jetpack.writeAsync(destinationFilePath, "");
    }).then(() => {
        return jetpack.removeAsync(destinationFilePath);
    }).then(() => {
        return new Promise((resolve, reject) => {
            request.get(sourceURL)
                .on("error", reject)
                .pipe(jetpack.createWriteStream(destinationFilePath))
                .on("error", reject)
                .on("finish", resolve);
        });
    });
};

/* test */ module.exports = {
    snippet: downloadFile,
    snippetName: "downloadFile",
    snippetTest: t => {
        const jetpack = require("fs-jetpack");
        t.plan(3);

        const URL = "https://cdnjs.cloudflare.com/ajax/libs/jquery/3.0.0/jquery.slim.min.js";
        return downloadFile(URL).then(() => {
            t.true(jetpack.exists("jquery.slim.min.js") === "file");
            const hash = snippets.sha256(jetpack.read("jquery.slim.min.js"));
            t.is(hash, "45fe0169d7f20adb2f1e63bcf4151971b62f34dbd9bce4f4f002df133bc2b03d");
            jetpack.remove("jquery.slim.min.js");
            t.pass();
        });
    }
};

/**
 * Shorten a string to a certain length (truncating it and adding "...").
 * 
 * @param {string} str The string to be shortened
 * @param {int} length The desired length (including the "...")
 * @return {string} A string whose length is at most the given length.
 * 
 * @example
 * 
 * ```
 * ellipsis("Hello World!", 11); // "Hello Wo..."
 * ellipsis("Hello", 8); // "Hello"
 * ellipsis("Hello World", 11); // "Hello World"
 * ```
 */
const ellipsis = function(str, maxlength) {
    return str.length > maxlength ? str.substring(0, maxlength - 3) + "..." : str;
};

/* test */ module.exports = {
    snippet: ellipsis,
    snippetName: "ellipsis",
    snippetTest: t => {
        t.is(ellipsis("Hello World!", 11), "Hello Wo...");
        t.is(ellipsis("Hello", 8), "Hello");
        t.is(ellipsis("Hello World", 11), "Hello World");
    }
};

/**
 * Executes a system command and returns a promise for its stdout output.
 * Output to stderr is ignored.
 * 
 * @param {string} command The command to execute
 * 
 * @return {Promise<string>} A promise for the output. Rejects if the command
 * exits with nonzero code.
 */
const execAsync = function(command) {
    const { exec } = require('child_process');
    return new Promise((resolve, reject) => {
        exec(command, (err, stdout, stderr) => { // eslint-disable-line no-unused-vars
            if (err) {
                reject(err);
            } else {
                resolve(stdout);
            }
        });
    });
};

/* test */ module.exports = {
    snippet: execAsync,
    snippetName: "execAsync",
    snippetTest: t => {
        t.plan(2);
        return Promise.all([
            execAsync("node -v").then(result => {
                t.true(/\d+\.\d+\.\d+/.test(result));
            }),
            execAsync("node --non-existent-option").catch(() => {
                t.pass();
            }),
        ]);
    }
};

/**
 * Filter an array in-place.
 * 
 * @requires snippet:replace-array
 * 
 * @param {function} filterFunction The filter function.
 * 
 * @return {Array} Returns the same array.
 * 
 * @example
 * 
 * ```
 * let x = [1, -1, -2, 3];
 * filterInPlace(x, v => v > 0);
 * console.log(x); // [1, 3]
 * ```
 */
const filterInPlace = function(array, filterFunction) {
    return snippets.replaceArray(array, array.filter(filterFunction));
};

/* test */ module.exports = {
    snippet: filterInPlace,
    snippetName: "filterInPlace",
    snippetTest: t => {
        const x = [1, -1, -2, 3];
        filterInPlace(x, v => v > 0);
        t.deepEqual(x, [1, 3]);
    }
};

examples:
```
let x = [1, -1, -2, 3];
filterInPlace(x, v => v > 0);
console.log(x); // [1, 3]
```

