/**
 * A collection of methods to work with overlays.
 * 
 * @property {function(element: HTMLElement, options: object): HTMLDivElement} createFor (see the code inside for documentation)
 * @property {object} createFor.defaultOptions (see the code inside for documentation)
 * @property {function(element: HTMLElement): number} removeAllFor (see the code inside for documentation)
 */
const overlays = (() => {

    /* global document, window */

    const overlays = {};

    const overlayMap = new WeakMap();

    function registerOverlay(element, overlay) {
        if (!overlayMap.has(element)) {
            overlayMap.set(element, new Set([overlay]));
        } else {
            overlayMap.get(element).add(overlay);
        }
    }

    /**
     * Create an overlay div covering the given element and returns it, so you can add styles or
     * do something else. Automatically detects the given element's z-index and copies it, to ensure
     * that the overlay gets above the element.
     * 
     * @param {HTMLElement | string} element The element, or a query selector for it.
     * @param {object} [options] An object with options. See `overlays.createFor.defaultOptions` to learn more.
     * 
     * @return {HTMLDivElement} The created overlay
     */
    overlays.createFor = function(element, options) {
        options = Object.assign({}, options || {}, overlays.createFor.defaultOptions);
        if (typeof element === "string") element = document.querySelector(element);
        const rect = element.getBoundingClientRect();
        const overlay = document.createElement("div");
        overlay.style.width = (rect.right - rect.left) + "px";
        overlay.style.height = (rect.bottom - rect.top) + "px";
        overlay.style.position = "absolute";
        overlay.style.top = rect.top + "px";
        overlay.style.left = rect.left + "px";
        overlay.style.zIndex = window.getComputedStyle(element).zIndex;
        overlay.className = options.classNames;
        document.documentElement.appendChild(overlay);
        registerOverlay(element, overlay);
        return overlay;
    };

    /**
     * The default options to be used by `overlays.createFor`.
     * 
     * @type {object}
     * 
     * @property {string | Array<string>} classNames CSS classes to apply on the element. This can be an array of class names or a string of space-separated class names.
     */
    overlays.createFor.defaultOptions = {
        classNames: "overlay"
    };

    /**
     * Remove all overlays present in the given element.
     * 
     * @param {HTMLElement | string} element The element, or a query selector for it.
     * 
     * @return {number} The amount of removed overlays
     */
    overlays.removeAllFor = function(element) {
        if (typeof element === "string") element = document.querySelector(element);
        if (!overlayMap.has(element)) return 0;
        const amount = overlayMap.get(element).size;
        for (const overlay of overlayMap.get(element)) {
            overlay.remove();
        }
        return amount;
    };

    return overlays;

})();

/* test */ module.exports = {
    snippet: overlays,
    snippetName: "overlays",
    snippetTest: t => {
        t.pass();
    }
};

/**
 * Pick a random subset array of the given array.
 * 
 * @requires snippet:random-int
 * 
 * @param {Array} array The source array.
 * 
 * @param {object} [options] An object specifying options.
 * @param {int} [options.length] If specified, defines the length of the subset to be taken.
 * If omitted (undefined), the length of the subset will be defined randomly between
 * options.minLength and array.length (inclusive)
 * @param {int} [options.minLength=0] This option is only relevant if options.length is
 * undefined. This option defines the minimum length of the picked subset. A common use case
 * is to set this option to 1 instead of the default 0 to disallow the empty subset to be
 * taken.
 * 
 * @returns {Array} An array whose elements consist of a random subset of the given array.
 * 
 * @throws {Error} If options.length or options.minLength is greater than array.length.
 */
const pickRandomSubset = function(array, options = undefined) {
    // Edited from https://github.com/Jam3/random-array-subset

    let length = options && options.length !== undefined ? options.length : undefined;
    const minLength = options && options.minLength !== undefined ? options.minLength : 0;

    if (length === undefined) {
        if (minLength > array.length) {
            throw new Error("The specified minLength can't be greater than the array length.");
        }
        length = snippets.randomInt(minLength, array.length + 1);
    } else if (length > array.length) {
        throw new Error("The specified length can't be greater than the array length.");
    }
    array = array.slice();
    const result = [];
    while (result.length < length) {
        const index = snippets.randomInt(0, array.length);
        result.push(array[index]);
        array.splice(index, 1);
    }
    return result;
};

/* test */ module.exports = {
    snippet: pickRandomSubset,
    snippetName: "pickRandomSubset",
    snippetTest: t => {
        const array = [{}, {}, {}, {}];
        for (let i = 0; i < 20; i++) {
            t.true(snippets.arrayHas(array, ...pickRandomSubset(array)));
            t.is(pickRandomSubset(array, { length: 3 }).length, 3);
            t.is(pickRandomSubset(array, { length: 0 }).length, 0);
            t.true(pickRandomSubset(array, { minLength: 1 }).length > 0);
        }
    }
};

/**
 * Pick a random element from the given array (using a uniform distribution
 * produced by `Math.random()`).
 * 
 * @param {Array} array The array.
 * 
 * @returns {*} A random element from the given array.
