/**
 * Converts a given string to a regex that matches it in a diacritic-insensitive way.
 * 
 * @param {string} string The string to be matched
 * 
 * @return {RegExp} The regexp that matches the string in a diacritic-insensitive way
 * 
 * @example
 * 
 * ```
 * toDiacriticsInsensitiveRegex("resume") === /r[Ee]s[Uu]m[Ee]/i
 * ```
 */
const toDiacriticsInsensitiveRegex = (() => {
    // Adapted from https://github.com/pocesar/js-diacritic-regex (thank you very much!)
    const charsToEscape = "-[]{}()*+!<=:?./\\^$|#,";
    const mappings = {
        "a": "Aa", // "a": String.fromCharCode(65, 97, 192, 224, 193, 225, 194, 226, 195, 227, 196, 228),
        "c": "Cc", // "e": String.fromCharCode(69, 101, 200, 232, 201, 233, 202, 234, 203, 235),
        "e": "Ee", // "i": String.fromCharCode(73, 105, 204, 236, 205, 237, 206, 238, 207, 239),
        "i": "Ii", // "o": String.fromCharCode(79, 111, 210, 242, 211, 243, 212, 244, 213, 245, 214, 246),
        "n": "Nn", // "n": String.fromCharCode(78, 110, 209, 241),
        "o": "Oo", // "u": String.fromCharCode(85, 117, 217, 249, 218, 250, 219, 251, 220, 252),
        "u": "Uu", // "c": String.fromCharCode(67, 99, 199, 231),
        "y": "Yy" // "y": String.fromCharCode(89, 121, 221, 253, 159, 255)
    };
    return string => {
        const regexString = string.split("").map(char => {
            if (charsToEscape.indexOf(char) !== -1) return `\\${char}`;
            for (const key in mappings) {
                if (key === char || mappings[key].indexOf(char) !== -1) {
                    return `[${mappings[key]}]`;
                }
            }
            return char;
        }).join("");
        return new RegExp(regexString, "i");
    };
})();

/* test */ module.exports = {
    snippet: toDiacriticsInsensitiveRegex,
    snippetName: "toDiacriticsInsensitiveRegex",
    snippetTest: t => {
        t.is(toDiacriticsInsensitiveRegex("resume").toString(), `/r[Ee]s[Uu]m[Ee]/i`);
        t.is(toDiacriticsInsensitiveRegex("a+b?").toString(), `/[Aa]\\+b\\?/i`);
        t.is(toDiacriticsInsensitiveRegex("match.me(\\'\\')").toString(), `/m[Aa]t[Cc]h\\.m[Ee]\\(\\\\'\\\\'\\)/i`);
    }
};

/**
 * Unzips a given zip file into the destination folder.
 * 
 * @requires npm:extract-zip^1.6.7
 * @requires npm:fs-jetpack^2.2.0
 * 
 * @param {string} zipFilePath The path to the zip file, as an
 * absolute path or a relative path to the given cwd (current
 * working directory).
 * @param {string} [destFolter="."] The destination folder, into
 * which the unzipped files will be put, as an absolute path or
 * a relative path to the given cwd (current working directory).
 * Will be created if it does not exist.
 * @param {string} [cwd="."] The reference path to be used for
 * internally computing the absolute paths of the first two
 * parameters. This parameter itself can be given as an absolute
 * path or a relative path to `process.cwd()`.
 * 
 * @return {Promise} A promise that resolves when the unzipping is
 * complete as rejects if any error happens in the process.
 */
const unzip = function(zipFilePath, destFolder = ".", cwd = ".") {
    const jetpack = require("fs-jetpack");
    const extract = require("extract-zip");

    const localJetpack = jetpack.cwd(cwd);
    return localJetpack.dirAsync(destFolder).then(() => new Promise((resolve, reject) => {
        extract(localJetpack.path(zipFilePath), { dir: localJetpack.path(destFolder) }, function(err) {
            if (err) {
                reject(err);
            } else {
                resolve();
            }
        });
    }));
};

/* test */ module.exports = {
    snippet: unzip,
    snippetName: "unzip",
    snippetTest: t => {
        const jetpack = require("fs-jetpack");
        t.plan(3);
        return unzip("test-fixtures/example-zip-file.zip", "test-temp").then(() => {
            t.is(jetpack.exists("test-temp/example-file.txt"), "file");
            t.is(jetpack.read("test-temp/example-file.txt"), "example-content");
            jetpack.remove("test-temp");
            t.pass();
        });
    }
};

/**
 * Get a promise that resolves after a given amount of milliseconds.
 * 
 * @param {int} millis The amount of milliseconds to wait before resolving the promise.
 * @param {*} [value] A value to fulfill the promise with.
 * 
 * @return {Promise} A promise that resolves after the given amount of milliseconds with
 * the given value (if given) or undefined (if not given).
 */
const wait = function(millis, value = undefined) {
    return new Promise(resolve => {
        setTimeout(() => {
            resolve(value);
        }, millis);
    });
};

/* test */ module.exports = {
    snippet: wait,
    snippetName: "wait",
    snippetTest: t => {
        t.pass();
    }
};

/**
 * Convert the given value to string, adding zeros to the left of it
 * so that the resulting string has length equal to the given length
 * (or, if the number representation is longer than the given length,
 * simply return the number converted to string).
 * 
 * @param {*} value The value to be converted (usually a number)
 * @param {int} length The desired length
 * 
 * @return {string} A string representation of the value with
 * length greater than or equal to the given length.
 * 
 * @example
 * 
 * ```
 * zeroPadLeft(1234, 7); // "0001234"
 * zeroPadLeft(1234, 3); // "1234"
 * zeroPadLeft("Hello", 9); // "0000Hello"
 * ```
 */
const zeroPadLeft = function(value, length) {
    let str = "" + value;
    while (str.length < length) str = "0" + str;
    return str;
};

/* test */ module.exports = {
    snippet: zeroPadLeft,
    snippetName: "zeroPadLeft",
    snippetTest: t => {
        t.is(zeroPadLeft(1234, 7), "0001234");
        t.is(zeroPadLeft(1234, 3), "1234");
        t.is(zeroPadLeft("Hello", 9), "0000Hello");
    }
};
